    /* Parse JSON */
    return JSON.parse(content);
  } catch (e) {
    /* กรณี Error ให้คืนค่าว่างหรือข้อความเดิม เพื่อไม่ให้ Workflow พัง */
    return {
      "result": "Error: Unable to process text.",
      "error": true
    };
  }
})()
